(function executeRule(current, previous /*null when async*/) {

	//var httpSession = gs.getSession().getHttpSession();
	//var attachments = httpSession.getAttribute('att_guest_acls_attachments') || [];
	var attachments = gs.getSession().getClientData('att_guest_acls_attachments');
	if (attachments) {
		// Note: String case is required here because we get a Java-String from the session-API!
		attachments = String(attachments).split(',');
	} else {
		attachments = [];
	}

	// it turned out that while the thumb-generation is initiated by the guest user (and 
	// the resulting record is created by the guest-user), this happens in a separate session
	// and as such won't count towards the actual guest users attachments
	//
	// var fileName = current.file_name.toString();
	// var record = current.table_sys_id.toString();
	// if (current.table_name == 'sys_attachment' && attachments.includes(record) && fileName.startsWith('thumb_')) {
	// 	var parts = fileName.split('_');
	// 	if (parts.length > 1) {
	// 		if (parts[1] == record && attachments.indexOf(parts[1]) != -1) {
	// 			// this is a thumb file generated by the system
	// 			return;
	// 		}
	// 	}
	// }

	attachments.push(current.getUniqueValue());
	//httpSession.setAttribute('att_guest_acls_attachments', attachments);
	gs.getSession().putClientData('att_guest_acls_attachments', attachments.join(','));

})(current, previous);
