sys_security_acl.script:
(function (attGr, rootRule) {

	// var attachments = gs.getSession().getHttpSession().getAttribute('att_guest_acls_attachments') || [];
	var attachments = gs.getSession().getClientData('att_guest_acls_attachments') || '';
	if (attachments.indexOf(attGr.getUniqueValue()) != -1) {
		return true;
	} else if (attachments) {
		// thumb file access on own attachments is allowed
		attachments = String(attachments).split(',');
		return attachments.includes(attGr.table_sys_id);
	}

	return false;

})(current, root_rule);
